import React, { useState } from "react";
import axios from "axios";
import Editor from "./Editor";
import "./index.css";

export default function App() {

  const [output, setOutput] = useState("");
  const [files, setFiles] = useState([]);

  // REAL CODE EXECUTION
  const runCode = async (code) => {

    try {

      const res = await axios.post("http://localhost:5000/execute", { code });

      setOutput(res.data.output);

    } catch (err) {

      setOutput("Server error: backend not running");

    }
  };


  // REAL PLAGIARISM
  const checkPlagiarism = async () => {

    const formData = new FormData();

    for (let f of files) {
      formData.append("files", f);
    }

    try {

      const res = await axios.post(
        "http://localhost:5000/plagiarism",
        formData
      );

      if (!res.data.plagiarismFound)
        setOutput("No plagiarism detected");

      else {

        let txt = "Plagiarism Found:\n";

        res.data.matches.forEach(m => {
          txt += `${m.file1} ↔ ${m.file2} = ${m.similarity}\n`;
        });

        setOutput(txt);
      }

    } catch {
      setOutput("Upload failed");
    }
  };


  return (

    <div className="min-h-screen bg-gray-950 text-white p-6">

      {/* ⭐ YOUR PURPLE HEADING */}
      <h1 className="text-4xl font-bold text-purple-400 text-center mb-8">
        Secure Coding Platform
      </h1>


      {/* ⭐ MAIN GRID (same layout as your image) */}
      <div className="grid md:grid-cols-2 gap-6">


        {/* LEFT SIDE — EDITOR */}
        <div className="bg-gray-900 p-4 rounded-xl shadow">

          <h2 className="mb-2 text-lg">Code • Execute • Detect Plagiarism</h2>

          <Editor onRun={runCode} />

        </div>


        {/* RIGHT SIDE — OUTPUT */}
        <div className="bg-gray-900 p-4 rounded-xl shadow">

          <h2 className="mb-2 text-lg">Output</h2>

          <pre className="bg-black p-4 rounded h-64 overflow-auto">
            {output}
          </pre>

        </div>


      </div>


      {/* ⭐ PLAGIARISM BLOCK BELOW (same as photo) */}
      <div className="bg-gray-900 mt-10 p-6 rounded-xl shadow max-w-md">

        <h2 className="text-xl mb-3">Plagiarism Detection</h2>

        <input
          type="file"
          multiple
          onChange={(e) => setFiles(e.target.files)}
          className="mb-3"
        />

        <br/>

        <button
          onClick={checkPlagiarism}
          className="bg-red-600 px-5 py-2 rounded"
        >
          Check Plagiarism
        </button>

      </div>

    </div>
  );
}
